from jinja2 import Template

def render_message(template_string, context):
    t = Template(template_string, autoescape=False)
    return t.render(context)

if __name__ == '__main__':
    message_template = """<p>{{message}}</p>"""
    message_context = {'message': '<h1>Hello!</h1>'}
    print(render_message(message_template, message_context))

