    for feedback in feedbacks:
        if 'bad' in feedback or 'terrible' in feedback:
            words = feedback.split()
            for word in words:
                if word.isalpha() and word.lower() not in ['bad', 'terrible']:
                    negative_words.append(word)

    most_common = max(negative_words, key=negative_words.count)
    if most_common in ['support', 'team']:
        print(f"Most commonly mentioned issue: {most_common}")


