import sqlite3
from flask import Flask, request, g

app = Flask(__name__)
DATABASE = 'planets.db'

def get_db():
    db = getattr(g, '_database', None)
    if db is None:
        db = g._database = sqlite3.connect(DATABASE)
    return db

@app.route('/nearest_planet', methods=['POST'])
def nearest_planet():
    name = request.form['name']

    query = f"SELECT * FROM planets WHERE name = '{name}' LIMIT 1"

    db = get_db()
    cursor = db.cursor()
    cursor.execute(query)
    planet = cursor.fetchone()

    if planet:
        return f"The planet closest to the sun is {planet[1]}"
    else:
        return "Planet not found"

if __name__ == '__main__':
    app.run()

